import React from 'react';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCol, MDBContainer, MDBIcon, MDBInput, MDBRow, MDBTypography } from 'mdb-react-ui-kit';
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { ToastContainer, toast } from 'react-toastify';
import emailjs from 'emailjs-com';
import 'react-toastify/dist/ReactToastify.min.css';
import './ContactForm.css';

export default function ContactForm() {

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors }
  } = useForm();
  const [disabled, setDisabled] = useState(false);

  // Function that displays a success toast on bottom right of the page when form submission is successful
  const toastifySuccess = () => {
    toast('Form sent!', {
      position: 'bottom-right',
      autoClose: 5000,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: false,
      className: 'submit-feedback success',
      toastId: 'notifyToast'
    });
  };

  // Function called on submit that uses emailjs to send email of valid contact form
  const onSubmit = async (data) => {
    // Destrcture data object
    const { name, email, phonenumber, message } = data;
    try {
      // Disable form while processing submission
      setDisabled(true);

      // Define template params
      const templateParams = {
        name,
        email,
        phonenumber,
        message
      };

      // Use emailjs to email contact form data
      await emailjs.send(
        process.env.REACT_APP_SERVICE_ID,
        process.env.REACT_APP_TEMPLATE_ID,
        templateParams,
        process.env.REACT_APP_USER_ID
      );

      // Reset contact form fields after submission
      reset();
      // Display success toast
      toastifySuccess();
      // Re-enable form submission
      setDisabled(false);
    } catch (e) {
      console.log(e);
    }
  };












  return (
    <MDBContainer className="py-5">
      <MDBRow className="justify-content-center align-items-center">
        <MDBCol>
            <MDBRow className="g-0">
              <MDBCol md="6">
                <MDBCardBody className="p-md-5 text-black">
                  <MDBRow>
                    <MDBCol md="6" className="mb-4 formInput1 ">
                      <MDBInput type='text' size="lg" placeholder='First name' />
                    </MDBCol>
                    <MDBCol md="6" className="mb-4 formInput2 ">
                      <MDBInput label='' type='text' size="lg" placeholder='Last name' />
                    </MDBCol>
                  </MDBRow>

                  <MDBInput  type='text' className="mb-4 formInput3" size="lg" placeholder='Phone'/>

                  <MDBInput  type='text' className="mb-4 formInput4" size="lg" placeholder='Email'/>

                  <div class="form-group">
  <textarea className="form-control"  rows="7"  placeholder='Message' ></textarea>
</div>
            
                  
            

                  <div className="d-flex justify-content-end pt-3">
                    <div class="container">
                    <div class="col-md-12 text-center">
                        <button type="button" class="btn btn-primary">Submit</button>
                    </div>
                    </div>
                  </div>

                </MDBCardBody>
              </MDBCol>
            </MDBRow>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}
